    Chogath = { id = 31, name = 'Chogath', displayName = "Cho'Gath", title = 'the Terror of the Void', iconPath = 'Chogath_Square_0.png', portraitPath = 'Chogath_0.jpg', splashPath = 'Chogath_Splash_0.jpg', danceVideoPath = '0031_dance.mp4', tags = 'fighter,mage,melee', range = 125.0, moveSpeed = 345.0, armorBase = 19.0, armorLevel = 3.5, manaBase = 205.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.29, manaRegenLevel = 0.09, healthRegenBase = 1.5, healthRegenLevel = 0.17, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 440.0, healthLevel = 80.0, attackBase = 54.1, attackLevel = 4.2, ratingDefense = 4, ratingMagic = 8, ratingDifficulty = 7, ratingAttack = 3, tips = "*Use of Rupture and Feral Scream can lead to mana starvation, but if partnered with a champion such as Soraka or one with a Mana Manipulator, using both skills early can yield positive results.*If you're having difficulty feasting upon champions, try eating minions until you're larger.*Using Rupture on creeps in combination with Carnivore is a good way to gain health and mana.", opponentTips = "*Purchasing a few HP items decreases the chances of Cho'Gath killing you quickly.*Focus on preventing Cho'Gath from reaching his max size, or obtaining it again after you've killed him.*Rupture has a smoke cloud indicating the area it will strike. Try to watch out for it to prevent Cho'Gath from being able to combo his abilities.", selectSoundPath = 'en_US/Chogath.mp3',  },
