    Skarner = { id = 72, name = 'Skarner', displayName = 'Skarner', title = 'the Crystal Vanguard', iconPath = 'Skarner_Square_0.png', portraitPath = 'Skarner_0.jpg', splashPath = 'Skarner_Splash_0.jpg', danceVideoPath = '0072_dance.mp4', tags = 'fighter,jungler,melee', range = 125.0, moveSpeed = 345.0, armorBase = 19.0, armorLevel = 3.8, manaBase = 205.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.29, manaRegenLevel = 0.09, healthRegenBase = 1.5, healthRegenLevel = 0.17, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 440.0, healthLevel = 96.0, attackBase = 54.1, attackLevel = 4.2, ratingDefense = 6, ratingMagic = 5, ratingDifficulty = 5, ratingAttack = 7, tips = '*Your basic attacks will reduce the cooldown of your abilities--use basic attacks between Crystal Slashes for maximum damage.*Hit a number of enemies with Fracture, and then follow up with a Crystal Slash to quickly gain a lot of health.*Impale is extremely powerful when you use it to position an enemy so your allies can attack them.', opponentTips = "*Skarner can't harm you very much outside of short range, so stay away from him!*Skarner's movement speed and attack speed are increased as long as his shield, Crystalline Exoskeleton, persists.  Attack him to lower his speed.*Some Skarners will have lots of defensive items, while others may build offensively. Check the items your enemy has to determine how to beat them.", selectSoundPath = 'en_US/Skarner.mp3',  },
    Heimerdinger = { id = 74, name = 'Heimerdinger', displayName = 'Heimerdinger', title = 'the Revered Inventor', iconPath = 'Heimerdinger_Square_0.png', portraitPath = 'Heimerdinger_0.jpg', splashPath = 'Heimerdinger_Splash_0.jpg', danceVideoPath = '0074_dance.mp4', tags = 'mage,pusher,ranged', range = 550.0, moveSpeed = 325.0, armorBase = 7.0, armorLevel = 3.0, manaBase = 240.0, manaLevel = 65.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.13, healthRegenBase = 0.9, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 350.0, healthLevel = 75.0, attackBase = 49.24, attackLevel = 3.0, ratingDefense = 6, ratingMagic = 8, ratingDifficulty = 4, ratingAttack = 2, tips = "*Turret placement can be a deciding factor in a battle. Against most enemies placing pairs of turrets is the best method, but if they have a lot of area of effect abilities your turrets can be destroyed quickly.*Placing turrets in brush can lead to a surprise attack in your favor.*The blind component of Concussive Grenade is very important. It doesn't need to stun a target to be effective, so try hitting the ranged attackers in the back.", opponentTips = "*Work with the players in your lane to bring down Heimerdinger's turrets as soon as he brings them up. They become more effective if they are allowed to cluster.*Killing Heimerdinger will destroy his turrets.  Try focusing him instead if his turrets get out of hand.", selectSoundPath = 'en_US/Heimerdinger.mp3',  },
    Nasus = { id = 75, name = 'Nasus', displayName = 'Nasus', title = 'the Curator of the Sands', iconPath = 'Nasus_Square_0.png', portraitPath = 'Nasus_0.jpg', splashPath = 'Nasus_Splash_0.jpg', danceVideoPath = '0075_dance.mp4', tags = 'fighter,pusher,melee,recommended', range = 125.0, moveSpeed = 350.0, armorBase = 15.0, armorLevel = 3.5, manaBase = 200.0, manaLevel = 45.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.32, manaRegenLevel = 0.1, healthRegenBase = 1.5, healthRegenLevel = 0.18, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 410.0, healthLevel = 90.0, attackBase = 53.3, attackLevel = 3.5, ratingDefense = 5, ratingMagic = 6, ratingDifficulty = 2, ratingAttack = 7, tips = "*Paying attention to last hitting with Siphoning Strike will have a large impact at the end of a game.*If you're solo, Spirit Fire is a great way to farm a lane. It can be bad if you're in a lane with 2 and you push too far. Find the right balance between Siphoning Strike last hits and AE farming.*If you have low defenses, people will focus you down even during your ultimate. Try buying some survivability items even in your DPS builds.", opponentTips = '*While transformed by his ultimate, Nasus is stronger than most champions in the League. Engage him only if you have a clear advantage.*Max rank Wither is a very effective counter to Attack Damage characters, so try to avoid getting caught alone by it.*Nasus is prone to being kited. Try not to engage him at full health.', selectSoundPath = 'en_US/Nasus.mp3',  },
    Nidalee = { id = 76, name = 'Nidalee', displayName = 'Nidalee', title = 'the Bestial Huntress', iconPath = 'Nidalee_Square_0.png', portraitPath = 'Nidalee_0.jpg', splashPath = 'Nidalee_Splash_0.jpg', danceVideoPath = '0076_dance.mp4', tags = 'support,pusher', range = 525.0, moveSpeed = 335.0, armorBase = 11.0, armorLevel = 3.5, manaBase = 220.0, manaLevel = 45.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.1, healthRegenBase = 1.0, healthRegenLevel = 0.12, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 370.0, healthLevel = 90.0, attackBase = 49.0, attackLevel = 3.5, ratingDefense = 4, ratingMagic = 7, ratingDifficulty = 7, ratingAttack = 5, tips = "*Use brush to your advantage!  Moving in and out of brush can heavily alter your effectiveness in battle.*In team fights start by harassing with your Javelin Toss, and then switch to cougar form to chase down anyone who flees.*Cougar abilities have no cost associated with them, allowing you to maintain a full stack of Guinsoo's Rageblade for extended periods of time.", opponentTips = "*Nidalee's traps are difficult to see, but are visible to both teams.*Nidalee's Javelin Toss deals more damage the farther she is. If she is far away, it's imperative to dodge it.*It's difficult to catch a Nidalee pouncing away, so be selective about when to pursue her.", selectSoundPath = 'en_US/Nidalee.mp3',  },
    Udyr = { id = 77, name = 'Udyr', displayName = 'Udyr', title = 'the Animal Spirit', iconPath = 'Udyr_Square_0.png', portraitPath = 'Udyr_0.jpg', splashPath = 'Udyr_Splash_0.jpg', danceVideoPath = '0077_dance.mp4', tags = 'fighter,melee,jungler', range = 125.0, moveSpeed = 345.0, armorBase = 14.75, armorLevel = 4.0, manaBase = 220.0, manaLevel = 30.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.28, manaRegenLevel = 0.09, healthRegenBase = 1.49, healthRegenLevel = 0.15, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 427.0, healthLevel = 99.0, attackBase = 52.91, attackLevel = 3.2, ratingDefense = 8, ratingMagic = 5, ratingDifficulty = 7, ratingAttack = 6, tips = '*Damage is applied to turtle shield post-mitigation. Therefore, buying defensive items can drastically increase your survivability.*Udyr is one of the best junglers in the game. Taking advantage of this can give your team a large XP advantage and map control.', opponentTips = '*Udyr players will often start in the jungle and only appear to gank in lanes. Try warding key locations to keep track of his movement.*Once the initial shield effect has faded, Turtle Stance is weak against other champions. Press the attack to force him to switch stances and prevent him from regenerating health and mana.*Some Udyr players buy tank items to become very durable. If they do, focus their weaker teammates instead.', selectSoundPath = 'en_US/Udyr.mp3',  },
    Poppy = { id = 78, name = 'Poppy', displayName = 'Poppy', title = 'the Iron Ambassador', iconPath = 'Poppy_Square_0.png', portraitPath = 'Poppy_0.jpg', splashPath = 'Poppy_Splash_0.jpg', danceVideoPath = '0078_dance.mp4', tags = 'fighter,assassin,melee', range = 125.0, moveSpeed = 345.0, armorBase = 18.0, armorLevel = 4.0, manaBase = 185.0, manaLevel = 30.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.28, manaRegenLevel = 0.09, healthRegenBase = 1.49, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 423.0, healthLevel = 81.0, attackBase = 56.3, attackLevel = 3.375, ratingDefense = 5, ratingMagic = 8, ratingDifficulty = 6, ratingAttack = 4, tips = "*Diplomatic Immunity can be used both defensively and offensively. It can be valuable to select a high damage target and try to kill them.*Try using Paragon of Demacia's movement speed burst to position your Heroic Charge.", opponentTips = '*The target of Diplomatic Immunity can still disable and deal damage to Poppy.  Meanwhile, Poppy is usually very aggressive while the spell is active.  Try to punish her for selecting you as the target.*Fighting against Poppy in the jungle can be dangerous due to Heroic Charge.  Try to catch her out in the open.', selectSoundPath = 'en_US/Poppy.mp3',  },
    Gragas = { id = 79, name = 'Gragas', displayName = 'Gragas', title = 'the Rabble Rouser', iconPath = 'Gragas_Square_0.png', portraitPath = 'Gragas_0.jpg', splashPath = 'Gragas_Splash_0.jpg', danceVideoPath = '0079_dance.mp4', tags = 'fighter,melee', range = 125.0, moveSpeed = 340.0, armorBase = 16.0, armorLevel = 3.6, manaBase = 221.0, manaLevel = 47.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.29, manaRegenLevel = 0.09, healthRegenBase = 1.45, healthRegenLevel = 0.17, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 434.0, healthLevel = 89.0, attackBase = 55.78, attackLevel = 3.375, ratingDefense = 9, ratingMagic = 6, ratingDifficulty = 6, ratingAttack = 4, tips = '*The buff from Drunken Rage is applied at the end of the channel, so try to use it before going into fight.*Try to knock enemies back into your towers with Explosive Cask.*Try to combo Body Slam with Explosive Cask to set up kills for your team.', opponentTips = "*Gragas can knock everybody away with his ultimate.  Be careful that you don't get knocked into him or, worse, an enemy tower.*Body Slam is on a very low cooldown, making it difficult to pursue Gragas. Don't overextend yourself chasing him.", selectSoundPath = 'en_US/Gragas.mp3',  },
    Pantheon = { id = 80, name = 'Pantheon', displayName = 'Pantheon', title = 'the Artisan of War', iconPath = 'Pantheon_Square_0.png', portraitPath = 'Pantheon_0.jpg', splashPath = 'Pantheon_Splash_0.jpg', danceVideoPath = '0080_dance.mp4', tags = 'assassin,melee', range = 150.0, moveSpeed = 355.0, armorBase = 17.1, armorLevel = 3.9, manaBase = 210.0, manaLevel = 34.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.32, manaRegenLevel = 0.09, healthRegenBase = 1.35, healthRegenLevel = 0.13, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 433.0, healthLevel = 87.0, attackBase = 50.7, attackLevel = 2.9, ratingDefense = 3, ratingMagic = 3, ratingDifficulty = 3, ratingAttack = 9, tips = '*Using Aegis of Zeonia quickly followed by Heartseeker Strike can devastate your enemies.*Using Spear Shot frequently in a battle, along with attack speed items, can help defensively by charging Aegis Protection.*Using Grand Skyfall to assist your allies across the map can quickly turn the tides of battle, putting your allies at an advantage.', opponentTips = '*Keeping yourself hidden in brush can make it harder for Pantheon to target you with Grand Skyfall.*Watch out for when Pantheon has a stunning or snaring partner in lane with him (example: Morgana, Sion) as he can cause devastating damage with his Heartseeker Strike.*Use your auto-attack to break his shield before casting your abilities.', selectSoundPath = 'en_US/Pantheon.mp3',  },
    Ezreal = { id = 81, name = 'Ezreal', displayName = 'Ezreal', title = 'the Prodigal Explorer', iconPath = 'Ezreal_Square_0.png', portraitPath = 'Ezreal_0.jpg', splashPath = 'Ezreal_Splash_0.jpg', danceVideoPath = '0081_dance.mp4', tags = 'carry,ranged', range = 550.0, moveSpeed = 325.0, armorBase = 12.0, armorLevel = 3.5, manaBase = 235.0, manaLevel = 45.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.13, healthRegenBase = 1.1, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 350.0, healthLevel = 80.0, attackBase = 47.2, attackLevel = 3.0, ratingDefense = 4, ratingMagic = 7, ratingDifficulty = 8, ratingAttack = 5, tips = '*Use Arcane Shift to help line up your other skill shots.*You can play Ezreal either as a Attack Damage carry or Ability Power carry depending on how you build him.*You can line up Trueshot Barrage to hit multiple minion waves or even Monsters.', opponentTips = "*Ezreal is a very fragile champion so take the fight to him.*Ezreal is completely skill shot based, so make sure to keep minions in between you.*Mystic Shot applies on-hit effects, including the red lizard buff on Summoner's Rift. Be careful in how you approach an Ezreal with Sigil buffs.", selectSoundPath = 'en_US/Ezreal.mp3',  },
    Mordekaiser = { id = 82, name = 'Mordekaiser', displayName = 'Mordekaiser', title = 'the Master of Metal', iconPath = 'Mordekaiser_Square_0.png', portraitPath = 'Mordekaiser_0.jpg', splashPath = 'Mordekaiser_Splash_0.jpg', danceVideoPath = '0082_dance.mp4', tags = 'fighter,pusher,melee', range = 125.0, moveSpeed = 340.0, armorBase = 15.0, armorLevel = 3.5, manaBase = 120.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 0.0, manaRegenLevel = 0.0, healthRegenBase = 1.49, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 421.0, healthLevel = 80.0, attackBase = 51.7, attackLevel = 3.5, ratingDefense = 6, ratingMagic = 6, ratingDifficulty = 3, ratingAttack = 6, tips = "*You can control a pet by holding down the alt key and using the right mouse button.*Sometimes the best defense is more offense; use Mordekaiser's abilities to charge up his shield.*Remember that Children of the Grave can be used defensively because of its lifesteal component.", opponentTips = "*Mordekaiser's abilities charge up his shield, making him tougher to kill over time. Try focusing fire on Mordekaiser to burst him down.*Keep your distance and don't stand near creep waves since he'll be aiming Siphon of Destruction at them.*Watch out for the Children of the Grave and Ignite combo. If you're low enough, this will kill you and give his team a powerful ghost.", selectSoundPath = 'en_US/Mordekaiser.mp3',  },
    Yorick = { id = 83, name = 'Yorick', displayName = 'Yorick', title = 'the Gravedigger', iconPath = 'Yorick_Square_0.png', portraitPath = 'Yorick_0.jpg', splashPath = 'Yorick_Splash_0.jpg', danceVideoPath = '0083_dance.mp4', tags = 'fighter,pusher,melee', range = 125.0, moveSpeed = 345.0, armorBase = 15.0, armorLevel = 3.6, manaBase = 235.0, manaLevel = 35.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.2, manaRegenLevel = 0.09, healthRegenBase = 1.4, healthRegenLevel = 0.14, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 421.0, healthLevel = 85.0, attackBase = 51.7, attackLevel = 3.5, ratingDefense = 6, ratingMagic = 6, ratingDifficulty = 3, ratingAttack = 6, tips = "*Omen of Pestilence can be used to block incoming skillshots.*Omen of Death can become more reliable if you cast it right before allies die, but it is less powerful.*During Omen of Death, both the Revenant and Reanimated champion count as a summon toward Unholy Covenant's bonus.", opponentTips = "*You can use Summoner Smite on Yorick's minions to immediately negate their effects.*When Yorick uses his ultimate on an ally, it may be easier to kill the Revenant before focusing back on the target who would otherwise revive.*The amount of health restored by Omen of Famine is based on the actual damage dealt, so Magic Resistance will lower its effectiveness.", selectSoundPath = 'en_US/Yorick.mp3',  },
    Akali = { id = 84, name = 'Akali', displayName = 'Akali', title = 'the Fist of Shadow', iconPath = 'Akali_Square_0.png', portraitPath = 'Akali_0.jpg', splashPath = 'Akali_Splash_0.jpg', danceVideoPath = '0084_dance.mp4', tags = 'assassin,melee,stealth', range = 125.0, moveSpeed = 350.0, armorBase = 16.5, armorLevel = 3.5, manaBase = 200.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 10.0, manaRegenLevel = 0.0, healthRegenBase = 1.45, healthRegenLevel = 0.13, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 445.0, healthLevel = 85.0, attackBase = 53.0, attackLevel = 3.2, ratingDefense = 3, ratingMagic = 8, ratingDifficulty = 6, ratingAttack = 5, tips = '*You can use runes and masteries to trigger her passive.*Akali excels at killing weaker characters. Let your team initiate and then strike at the people in the back.*Find the balance between storing Essence of Shadows, and using Shadow Dance before having max charges.', opponentTips = "*Akali can still be hit by area effect spells while invisible inside Twilight Shroud.*Akali greatly benefits from both Mejai's Soulstealer and the Sword of the Occult. Watch which items she gets and prioritize her accordingly.*Return to base if your Health is low and Akali has multiple charges of her ultimate.", selectSoundPath = 'en_US/Akali.mp3',  },
    Kennen = { id = 85, name = 'Kennen', displayName = 'Kennen', title = 'the Heart of the Tempest', iconPath = 'Kennen_Square_0.png', portraitPath = 'Kennen_0.jpg', splashPath = 'Kennen_Splash_0.jpg', danceVideoPath = '0085_dance.mp4', tags = 'mage,ranged', range = 550.0, moveSpeed = 335.0, armorBase = 14.0, armorLevel = 3.75, manaBase = 200.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 10.0, manaRegenLevel = 0.0, healthRegenBase = 0.93, healthRegenLevel = 0.13, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 403.0, healthLevel = 79.0, attackBase = 51.3, attackLevel = 3.3, ratingDefense = 3, ratingMagic = 7, ratingDifficulty = 5, ratingAttack = 4, tips = '*You can stun your opponents by inflicting 3 Marks of the Storm upon them.*Lightning Rush can be used to initiate fights with its Energy return component, allowing him to use other abilities afterward.*You can land an initial Mark of the Storm debuff on an opponent with Thundering Shuriken, and then add to it with Electrical Surge.', opponentTips = '*Consider playing a bit more cautiously around Kennen when you have a Mark of the Storm debuff.  If he hits you with 3 Marks of the Storm, you will get stunned.*Kennen is flimsy by nature - turn and attack him if he gets careless with Lightning Rush.', selectSoundPath = 'en_US/Kennen.mp3',  },
    Garen = { id = 86, name = 'Garen', displayName = 'Garen', title = 'The Might of Demacia', iconPath = 'Garen_Square_0.png', portraitPath = 'Garen_0.jpg', splashPath = 'Garen_Splash_0.jpg', danceVideoPath = '0086_dance.mp4', tags = 'fighter,melee,recommended', range = 125.0, moveSpeed = 345.0, armorBase = 19.0, armorLevel = 2.7, manaBase = 0.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 0.0, manaRegenLevel = 0.0, healthRegenBase = 1.49, healthRegenLevel = 0.15, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 455.0, healthLevel = 96.0, attackBase = 52.0, attackLevel = 3.5, ratingDefense = 4, ratingMagic = 2, ratingDifficulty = 2, ratingAttack = 9, tips = "*Garen's regeneration greatly increases if he can avoid receiving damage for several seconds.*Courage increases the effectivess of defensive items. Be sure to itemize appropriately against your opponents damage types.*Garen is only constrained by cooldowns, making items such as Youmuu's Ghostblade and Brutalizer very effective for him.", opponentTips = '*Stack armor items to lower the large amount of physical damage that Garen deals out.*Try to run away from Garen as your health gets lower, as he can execute you quickly with Demacian Justice.*Be careful about attacking Garen in brush. It often will lead to taking full damage from Judgment.', selectSoundPath = 'en_US/Garen.mp3',  },
    Leona = { id = 89, name = 'Leona', displayName = 'Leona', title = 'the Radiant Dawn', iconPath = 'Leona_Square_0.png', portraitPath = 'Leona_0.jpg', splashPath = 'Leona_Splash_0.jpg', danceVideoPath = '0089_dance.mp4', tags = 'tank,melee', range = 125.0, moveSpeed = 335.0, armorBase = 18.0, armorLevel = 3.1, manaBase = 235.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.14, healthRegenBase = 1.8, healthRegenLevel = 0.17, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 430.0, healthLevel = 87.0, attackBase = 55.0, attackLevel = 3.0, ratingDefense = 8, ratingMagic = 3, ratingDifficulty = 4, ratingAttack = 4, tips = '*Lead the charge and mark your foes with Sunlight before your allies deal damage.*Shield of Daybreak and Zenith Blade form a powerful offensive combo.*You can absorb a huge amount of damage using Eclipse, but you must stay near enemies to gain the bonus duration.', opponentTips = "*When Leona activates Eclipse, you have three seconds to get away from her before she deals damage.*Only foes in the center of Solar Flare get stunned, so you can often avoid this if you're quick.", selectSoundPath = 'en_US/Leona.mp3',  },
    Malzahar = { id = 90, name = 'Malzahar', displayName = 'Malzahar', title = 'the Prophet of the Void', iconPath = 'Malzahar_Square_0.png', portraitPath = 'Malzahar_0.jpg', splashPath = 'Malzahar_Splash_0.jpg', danceVideoPath = '0090_dance.mp4', tags = 'mage,ranged', range = 550.0, moveSpeed = 340.0, armorBase = 12.0, armorLevel = 3.5, manaBase = 250.0, manaLevel = 45.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.12, healthRegenBase = 0.9, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 380.0, healthLevel = 80.0, attackBase = 51.66, attackLevel = 3.0, ratingDefense = 2, ratingMagic = 9, ratingDifficulty = 6, ratingAttack = 2, tips = "*Don't be afraid of spamming skills to get your Voidlings into action.*Lay down a Null Zone before using Nether Grasp to maximize damage.*Null Zone is extremely potent against high HP neutral monsters. Try using this to get the blue buff from the Ancient Golem.", opponentTips = '*Voidlings have a limited lifespan of 21 seconds.*Avoid minion waves that have been affected by Malefic Visions. You never know when they may die and pass the Visions onto you.*Malzahar can be difficult to 1v1 if he has his ultimate up. Make sure you have enough health to survive a round of his spells.', selectSoundPath = 'en_US/Malzahar.mp3',  },
    Talon = { id = 91, name = 'Talon', displayName = 'Talon', title = "the Blade's Shadow", iconPath = 'Talon_Square_0.png', portraitPath = 'Talon_0.jpg', splashPath = 'Talon_Splash_0.jpg', danceVideoPath = '0091_dance.mp4', tags = 'assassin,melee', range = 125.0, moveSpeed = 350.0, armorBase = 17.0, armorLevel = 3.5, manaBase = 260.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.35, manaRegenLevel = 0.1, healthRegenBase = 1.45, healthRegenLevel = 0.15, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 440.0, healthLevel = 85.0, attackBase = 50.0, attackLevel = 3.1, ratingDefense = 5, ratingMagic = 2, ratingDifficulty = 6, ratingAttack = 9, tips = "*Noxian Diplomacy naturally works well with Cutthroat. Try activating Noxian Diplomacy before leaping on your opponent with Cutthroat.*Shadow Assault is a powerful escape tool, but can also be offensively used to assault a group.*Remember to pick your target before the fight. Focusing all of Talon's abilities on one target can be very rewarding, but splitting them up between many may leave you helpless.", opponentTips = "*Talon's attacks are all physical damage. Build armor early to counter his burst damage.*Talon is heavily reliant on Shadow Assault to escape a fight. When it is down he's significantly more vulnerable.*Talon has no healing or sustain abilities. Weakening him in lane could force him to return to base.", selectSoundPath = 'en_US/Talon.mp3',  },
    Riven = { id = 92, name = 'Riven', displayName = 'Riven', title = 'the Exile', iconPath = 'Riven_Square_0.png', portraitPath = 'Riven_0.jpg', splashPath = 'Riven_Splash_0.jpg', danceVideoPath = '0092_dance.mp4', tags = 'fighter,melee', range = 125.0, moveSpeed = 345.0, armorBase = 15.0, armorLevel = 3.2, manaBase = 0.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 0.0, manaRegenLevel = 0.0, healthRegenBase = 1.1, healthRegenLevel = 0.1, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 414.0, healthLevel = 86.0, attackBase = 54.0, attackLevel = 2.75, ratingDefense = 5, ratingMagic = 1, ratingDifficulty = 4, ratingAttack = 8, tips = "*Riven's Broken Wings locks onto the champion your cursor is over at the time of cast. If you want to move past your opponent, make sure that your cursor is past your opponent as well.*Riven lacks a source of healing or strong defense and must compensate with burst combos. Lead into fights with Broken Wings and Ki Burst, while using Valor to escape and absorb counter-harass.", opponentTips = "*Riven has a great deal of mobility but she cannot move very far with any one skill. Rooting or Silencing her during her combo will greatly reduce her effectiveness.*All of Riven's damage is physical, prioritize high armor if an opposing Riven gets out of control.*Riven excels at dueling multiple melee attackers at once, as she will be on an almost equal footing damage-wise. If pairing up against Riven, don't go in as double melee until she has expended her full combo.", selectSoundPath = 'en_US/Riven.mp3',  },
    KogMaw = { id = 96, name = 'KogMaw', displayName = "Kog'Maw", title = 'the Mouth of the Abyss', iconPath = 'KogMaw_Square_0.png', portraitPath = 'KogMaw_0.jpg', splashPath = 'KogMaw_Splash_0.jpg', danceVideoPath = '0096_dance.mp4', tags = 'ranged,carry', range = 500.0, moveSpeed = 330.0, armorBase = 10.0, armorLevel = 3.5, manaBase = 255.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.5, manaRegenLevel = 0.14, healthRegenBase = 1.0, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 440.0, healthLevel = 84.0, attackBase = 46.0, attackLevel = 3.0, ratingDefense = 2, ratingMagic = 5, ratingDifficulty = 8, ratingAttack = 8, tips = "*Kog'Maw can outrange most champions with his Bio-Arcane Barrage ability.*Use Void Ooze to set up a perfect Living artillery.*Make the most out of your Icathian Surprise.", opponentTips = "*Kog'Maw doesn't have a good escape ability, so he is especially prone to ganks.*Run away from Kog'Maw after he dies!!!!*The range on Living Artillery increases each rank.*Bio-Arcane Barrage allows Kog'Maw to kill Baron Nashor extremely early. It can be valuable to ward Nashor when Kog'Maw completes his Bloodrazor.", selectSoundPath = 'en_US/KogMaw.mp3',  },
    Shen = { id = 98, name = 'Shen', displayName = 'Shen', title = 'Eye of Twilight', iconPath = 'Shen_Square_0.png', portraitPath = 'Shen_0.jpg', splashPath = 'Shen_Splash_0.jpg', danceVideoPath = '0098_dance.mp4', tags = 'tank,support,melee', range = 125.0, moveSpeed = 335.0, armorBase = 15.0, armorLevel = 4.0, manaBase = 200.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 10.0, manaRegenLevel = 0.0, healthRegenBase = 1.49, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 428.0, healthLevel = 85.0, attackBase = 54.5, attackLevel = 3.375, ratingDefense = 9, ratingMagic = 3, ratingDifficulty = 3, ratingAttack = 3, tips = "*Shadow Dash restores more energy if it hits multiple enemy champions, so don't always use it on a single target.*Try to keep an eye on your allies' health, even if in remote lanes, as Stand United can be used anywhere on the map.  You can use F2-F5 to target them quickly.", opponentTips = "*Shen is a very durable champion, with multiple ways to absorb damage.  Be wary about attacking him when he seems weak.*He can also teleport to any ally's aid, regardless of location, so be aware that a 1v1 fight can turn into 1v2 very quickly with Shen.", selectSoundPath = 'en_US/Shen.mp3',  },
    Lux = { id = 99, name = 'Lux', displayName = 'Lux', title = 'the Lady of Luminosity', iconPath = 'Lux_Square_0.png', portraitPath = 'Lux_0.jpg', splashPath = 'Lux_Splash_0.jpg', danceVideoPath = '0099_dance.mp4', tags = 'mage,support,ranged', range = 550.0, moveSpeed = 340.0, armorBase = 8.0, armorLevel = 4.0, manaBase = 250.0, manaLevel = 50.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.2, manaRegenLevel = 0.12, healthRegenBase = 0.9, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 345.0, healthLevel = 79.0, attackBase = 50.0, attackLevel = 3.3, ratingDefense = 4, ratingMagic = 9, ratingDifficulty = 6, ratingAttack = 2, tips = "*Lux has great zone control abilities. Try to set up Lucent Singularity to prevent an enemy's advance or escape.*If you have trouble landing Prismatic Barrier, remember that it returns to you after it reaches max range.  Try positioning yourself to hit your allies with its return trip.*Lucent Singularity is a great scouting tool. Try throwing it into brush before walking into it to check for ambushes.", opponentTips = "*Lux has powerful zone control abilities.  Try to spread out and attack from different directions so she cannot lock down a specific area.*When retreating with low health, be prepared to dodge Lux's Final Spark, a red targeting beam fires prior to the main beam, so try to move to the side if possible.", selectSoundPath = 'en_US/Lux.mp3',  },
    Xerath = { id = 101, name = 'Xerath', displayName = 'Xerath', title = 'the Magus Ascendant', iconPath = 'Xerath_Square_0.png', portraitPath = 'Xerath_0.jpg', splashPath = 'Xerath_Splash_0.jpg', danceVideoPath = '0101_dance.mp4', tags = 'mage,ranged', range = 550.0, moveSpeed = 340.0, armorBase = 12.0, armorLevel = 3.5, manaBase = 250.0, manaLevel = 45.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.12, healthRegenBase = 0.9, healthRegenLevel = 0.11, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 380.0, healthLevel = 80.0, attackBase = 51.66, attackLevel = 3.0, ratingDefense = 3, ratingMagic = 10, ratingDifficulty = 6, ratingAttack = 1, tips = "*It's easier to land Arcanopulse on an enemy when they're moving toward or away from you, rather than side to side.*While casting Locus of Power you can begin lining up your next spell.*Rylai's Crystal Scepter will make landing Arcanopulse after Mage Chains easier as the target will be slowed.", opponentTips = "*Xerath's range can be intimidating, but most champions who close distance with him can trade favorably.*Xerath takes a long time to engage Locus of Power. Watch for his spellcast animation and start dodging.*Banshee's Veil will make it much harder for Xerath to combo Mage Chains on you.", selectSoundPath = 'en_US/Xerath.mp3',  },
    Shyvana = { id = 102, name = 'Shyvana', displayName = 'Shyvana', title = 'the Half-Dragon', iconPath = 'Shyvana_Square_0.png', portraitPath = 'Shyvana_0.jpg', splashPath = 'Shyvana_Splash_0.jpg', danceVideoPath = '0102_dance.mp4', tags = 'fighter,melee', range = 125.0, moveSpeed = 350.0, armorBase = 18.0, armorLevel = 3.35, manaBase = 100.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 0.0, manaRegenLevel = 0.0, healthRegenBase = 1.45, healthRegenLevel = 0.16, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 435.0, healthLevel = 95.0, attackBase = 55.0, attackLevel = 3.4, ratingDefense = 6, ratingMagic = 5, ratingDifficulty = 4, ratingAttack = 7, tips = "*Shyvana's abilities all benefit from attacking units. As a result, Attack Speed items are more valuable on her than most champions.*Flame Breath is great to level-up against difficult lane opponents.*Burnout is an ideal choice for jungling. Monsters will be hit by the full duration, and the speed boost improves her ganking potential.*It can be valuable to purchase one of the items that can slow enemies: Phage, Frozen Mallet, Trinity Force, or Entropy.", opponentTips = "*A significant portion of Shyvana's power is generated through aggression, making her weak against opponents who are able to keep their distance.*All of Shyvana's basic abilities hit multiple enemies in dragon form. It's important for opposing champions to not cluster while fighting her.*Shyvana's Fury Bar is an indicator for when her ultimate can be activated. Harassing her when she's low on Fury can be very effective.", selectSoundPath = 'en_US/Shyvana.mp3',  },
    Ahri = { id = 103, name = 'Ahri', displayName = 'Ahri', title = 'the Nine-Tailed Fox', iconPath = 'Ahri_Square_0.png', portraitPath = 'Ahri_0.jpg', splashPath = 'Ahri_Splash_0.jpg', danceVideoPath = '0103_dance.mp4', tags = 'assassin,mage,ranged', range = 550.0, moveSpeed = 330.0, armorBase = 11.0, armorLevel = 3.5, manaBase = 230.0, manaLevel = 50.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.25, manaRegenLevel = 0.12, healthRegenBase = 1.1, healthRegenLevel = 0.12, magicResistBase = 30.0, magicResistLevel = 0.0, healthBase = 380.0, healthLevel = 80.0, attackBase = 50.0, attackLevel = 3.0, ratingDefense = 4, ratingMagic = 8, ratingDifficulty = 8, ratingAttack = 3, tips = "*Use Charm to set up your combos, it will make landing Orb of Deception and Fox-Fire dramatically easier.*Initiate team fights using Charm, and chase down stragglers with Spirit Rush.*Spirit Rush enables Ahri's abilities, it opens up paths for Charm, helps double hitting with Orb of Deception, and closes to make use of Fox-Fire.", opponentTips = "*Ahri's survivability is dramatically reduced when her Ultimate, Spirit Rush, is down.*Stay behind minions to make Charm difficult to land, this will reduce Ahri's damage potential significantly.", selectSoundPath = 'en_US/Ahri.mp3',  },
