    Fizz = { id = 105, name = 'Fizz', displayName = 'Fizz', title = 'the Tidal Trickster', iconPath = 'Fizz_Square_0.png', portraitPath = 'Fizz_0.jpg', splashPath = 'Fizz_Splash_0.jpg', danceVideoPath = '0105_dance.mp4', tags = 'fighter,assassin,melee', range = 175.0, moveSpeed = 335.0, armorBase = 12.7, armorLevel = 3.4, manaBase = 200.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.22, manaRegenLevel = 0.09, healthRegenBase = 1.4, healthRegenLevel = 0.14, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 414.0, healthLevel = 86.0, attackBase = 53.0, attackLevel = 3.0, ratingDefense = 4, ratingMagic = 7, ratingDifficulty = 6, ratingAttack = 3, tips = "*Since Fizz can move through units, find opportunities in lane to walk through minions and apply your 'Seastone Trident' ability - the damage will rapidly add up as the opponent's health drops.*Fizz's ultimate ability, 'Chum the Waters', can be aimed at an enemy or towards the area where you think they'll be going.*Fizz's spells scale off ability power - consider getting items like Rod of Ages or Rylai's Crystal Scepter against teams that have high burst threat - and items like Rabadon's Deathcap if you think you can survive without the health.", opponentTips = "*Fizz becomes extremely deadly when your health reaches critical levels - each of Fizz's autoattacks apply a damage over time effect that rapidly grows in damage as your health becomes lower - Stay healthy when fighting Fizz, if possible.*Fizz is best in extended skirmishes - Don't chase Fizz if you can help it!", selectSoundPath = 'en_US/Fizz.mp3',  },
    Volibear = { id = 106, name = 'Volibear', displayName = 'Volibear', title = "the Thunder's Roar", iconPath = 'Volibear_Square_0.png', portraitPath = 'Volibear_0.jpg', splashPath = 'Volibear_Splash_0.jpg', danceVideoPath = '0106_dance.mp4', tags = 'tank,fighter,jungler,melee', range = 125.0, moveSpeed = 345.0, armorBase = 16.5, armorLevel = 3.5, manaBase = 220.0, manaLevel = 30.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.4, manaRegenLevel = 0.13, healthRegenBase = 1.4, healthRegenLevel = 0.13, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 440.0, healthLevel = 86.0, attackBase = 54.0, attackLevel = 3.3, ratingDefense = 7, ratingMagic = 4, ratingDifficulty = 4, ratingAttack = 6, tips = '*Lead the charge with Rolling Thunder and slow enemies with Majestic Roar.*Volibear benefits a great deal from additional health and cooldown reduction.*You must attack three times in rapid succession before Frenzy can be activated--having the toughness to survive for a long time is one way to accomplish this goal.', opponentTips = '*Volibear can gain great movement speed while chasing targets, but he gains much less of a bonus when running away.*Volibear regenerates a large amount of health when he drops to critical health.*Slowing or stunning Volibear when he tries to charge will greatly reduce his effectiveness.', selectSoundPath = 'en_US/Volibear.mp3',  },
    Rengar = { id = 107, name = 'Rengar', displayName = 'Rengar', title = 'the Pridestalker', iconPath = 'Rengar_Square_0.png', portraitPath = 'Rengar_0.jpg', splashPath = 'Rengar_Splash_0.jpg', danceVideoPath = '0107_dance.mp4', tags = 'fighter,jungler,melee,stealth', range = 125.0, moveSpeed = 345.0, armorBase = 16.0, armorLevel = 3.5, manaBase = 5.0, manaLevel = 0.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 0.0, manaRegenLevel = 0.0, healthRegenBase = 0.72, healthRegenLevel = 0.08, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 435.0, healthLevel = 85.0, attackBase = 55.0, attackLevel = 3.0, ratingDefense = 4, ratingMagic = 2, ratingDifficulty = 5, ratingAttack = 7, tips = "*Use Rengar's ultimate to find and assassinate high priority targets in team fights and skirmishes.*A lot of Rengar's power comes from empowered abilities he uses at the right moment - choose wisely!*Make sure to take paths through brush when chasing opponents to take advantage of Rengar's passive.", opponentTips = "*Rengar gains an empowered ability when his resource bar is full. Try to confront him when it's low.*Rengar's passive enables him to leap out of brush, so avoid fighting him when near bushes.*Rengar's ultimate grants him temporary stealth, so try to avoid traveling alone when injured.", selectSoundPath = 'en_US/Rengar.mp3',  },
