    Sejuani = { id = 113, name = 'Sejuani', displayName = 'Sejuani', title = "the Winter's Wrath", iconPath = 'Sejuani_Square_0.png', portraitPath = 'Sejuani_0.jpg', splashPath = 'Sejuani_Splash_0.jpg', danceVideoPath = '0113_dance.mp4', tags = 'tank,jungler,melee', range = 125.0, moveSpeed = 340.0, armorBase = 20.5, armorLevel = 3.5, manaBase = 220.0, manaLevel = 40.0, criticalChanceBase = 0.0, criticalChanceLevel = 0.0, manaRegenBase = 1.29, manaRegenLevel = 0.09, healthRegenBase = 1.45, healthRegenLevel = 0.17, magicResistBase = 30.0, magicResistLevel = 1.25, healthBase = 450.0, healthLevel = 85.0, attackBase = 54.0, attackLevel = 3.5, ratingDefense = 7, ratingMagic = 6, ratingDifficulty = 4, ratingAttack = 5, tips = '*Arctic Assault provides a lot of utility to Sejuani beyond its damage output. Use it to escape bad situations or chase runners.*Sejuani provides a strong gank coming out of the jungle. Start with a Cloth Armor and 5 Health Potions for a smooth run.*Save Glacial Prison for encounters where you have teammates present to take advantage of it.', opponentTips = '*Dodging Arctic Assault and Glacial Prison can give you a huge advantage. Master using movement abilities or Summoner spells like Flash when Sejuani tries to engage.*Stun, slow, or knockback Sejuani when she activates Northern Winds to reduce her ability to deal damage with it.', selectSoundPath = 'en_US/Sejuani.mp3',  },
